package qajob.First;

public class ParentEncapsulation extends Encapsulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ParentEncapsulation var=new ParentEncapsulation();
		var.setA(10);
		var.setName("Ghulam Hassan");
		System.out.println(var.getA());
		System.out.println(var.getName());
		System.out.println("That's Encapsulation Program");
	}

}
